import React, { useState } from 'react';
import './Search.css';

export default function Search(){
    const [activity, setActivity] = useState([])
    const getActivity = (e) => {
        setActivity([...activity, e.target.value])
    }
    const activities =  ["Hiking", "Boating", "Climbing", "Picnicking", "Skiing", "Swimming", "Off-Roading", "Camping","RV Camping","Biking","Fishing","Snorkeling","SCUBA Diving","Surfing","Geocaching","Wildlife Watching", "Horseback Riding","Museum Exhibits","Guided Tours","Hunting"]
    const options = activities.sort().map((value,index) => {
        return <div key={index} className="select-container">

        <label className="activity-label" htmlFor="activity-option">{value}
        <input id="activity-option" className="activity-option" type="checkbox" value={value}  /></label>
        </div>
    })
    return (
        <form className="search-form">
            <div className="activity-container">
                <div onChange={getActivity} className="activity-select" value={activity}>
                {options}
                </div>
            </div>
        </form>
    )
}
