import React, { useState } from 'react';
import './Search.css';

export default function Search(){
    const [activity, setActivity] = useState([])
    const [showActivities, setShowActivities] = useState(false)
    const getActivity = (e) => {
        setActivity([...activity, e.target.value])
    }
    const showMenu = (e) => {
        e.preventDefault();
        setShowActivities(true)
    }
    const activities =  ["Hiking", "Boating", "Climbing", "Picnicking", "Skiing", "Swimming", "Off-Roading", "Camping","RV Camping","Biking","Fishing","Snorkeling","SCUBA Diving","Surfing","Geocaching","Wildlife Watching", "Horseback Riding","Museum Exhibits","Guided Tours"]
    const options = activities.sort().map((value,index) => {
        return <div key={index} className="select-container control-me">
        <input id={`activity-option${index}`} className="activity-option" name={value} type="checkbox" value={value}  />
        <label className="activity-label" name={value} htmlFor={`activity-option${index}`}>{value}</label>
        <div className="control-me"></div>
        </div>
    })

    return (
        <form className="search-form">
            <div className="activity-container">
                <button>Activities</button>
              {showActivities &&
                <div onChange={getActivity} className="activity-select" value={activity}>
                 {options}
                </div>}
            </div>
        </form>
    )
}
