import React, { useState } from 'react';
import './Search.css';

export default function Search(){
    const [activity, setActivity] = useState([])
    const [check, setCheck] = useState(false)
    const getActivity = (e) => {
        setActivity([...activity, e.target.value])
    }
    const activities =  ["Hiking", "Boating", "Climbing", "Picnicking", "Skiing", "Swimming", "Off-Roading", "Camping","RV Camping","Biking","Fishing","Snorkeling","SCUBA Diving","Surfing","Geocaching","Wildlife Watching", "Horseback Riding","Museum Exhibits","Guided Tours","Hunting"]
    const options = activities.sort().map((value,index) => {
        return <div key={index} className="select-container control-me">
        <input id={`activity-option${index}`} className="activity-option" name={value} type="checkbox" value={value}  />
        <label className="activity-label" onClick={toggleCheck} name={value} htmlFor={`activity-option${index}`}>{value}</label>
        <div className="control-me"></div>
        </div>
    })
    const toggleCheck = () => {
        return check===true ? setCheck(false): check===false ? setCheck(true):check
    }
    return (
        <form className="search-form">
            <div className="activity-container">
                <div onChange={getActivity} className="activity-select" value={activity}>
                {options}
                </div>
            </div>
        </form>
    )
}
